function HabitPage() {
  const [habits, setHabits] = useState([]);

  const habitsCollection = collection(db, "habits");

  // Get all documents/habits in db.
  useEffect(() => {
    const getHabits = async () => {
      const data = await getDocs(habitsCollection);
      console.log(data);
      setHabits(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
    };

    getHabits();
  }, []);
  return (
    <section className="habit">
      {habits.map((user) => {
        return (
          <>
            <div className="habit__content">
              <button className="habit__button">
                <p className="habit__btn-title" key={user.id}>
                  {user.habit}
                </p>
                <img
                  key="uniqueId2"
                  className="habit__btn-icon"
                  src={user.url}
                  alt="habit icon"
