import React from "react";
import { Component } from "react";
import "../HomePage/HomePage.scss";

class HomePage extends Component {
  handleSubmit = (e) => {
    e.preventDefault();
    this.props.history.push("/habit");
  };

  render() {
    return (
      <>
        <div className="homepage">
          <div className="homepage__content">
            <div className="homepage__brand">
              <h1 className="homepage__brand-title">Sproutr</h1>
              <h2 className="homepage__brand-statement">
                Build &amp; Track the Habits that matter.
              </h2>
            </div>

            <div className="homepage__body">
              <h2 className="homepage__title">habÂ·it</h2>
              <h3 className="homepage__noun">noun</h3>
              <p className="homepage__definition">
                1. A habit is a routine of behavior that is repeated regularly
                and tends to occur subconsciously. The American Journal of
                Psychology defined a "habit, from the standpoint of psychology,
                [as] a more or less fixed way of thinking, willing, or feeling
                acquired through previous repetition of a mental experience."
              </p>
            </div>
          </div>
          <form className="homepage__form" onSubmit={this.handleSubmit}>
            <button className="homepage__submit">Let's Get Started</button>
          </form>
        </div>
      </>
    );
  }
}

export default HomePage;

