import React from "react";
import { Component } from "react";
import "../HomePage/HomePage.scss";

class HomePage extends Component {
  handleSubmit = (e) => {
    e.preventDefault();
    this.props.history.push("/habit");
  };

  render() {
    return (
      <>
        <div className="homepage">
          <div className="homepage__content">
            <div>
              <h1 className="brand__item-title">Habitr</h1>
              <h2 className="brand__item-statement">
                Build &amp; Track the Habits that matter.
              </h2>
            </div>

            <h2 className="homepage__title">habÂ·it</h2>
            <h3 className="homepage__noun">noun</h3>
            <p className="homepage__definition">
              1. a settled or regular tendency or practice, especially one that
              is hard to give up. "we stayed together out of habit"
            </p>
            <p className="homepage__definition">
              2. an automatic reaction to a specific situation.
            </p>
          </div>
          <form className="homepage__form" onSubmit={this.handleSubmit}>
            <button className="homepage__submit">Let's Get Started</button>
          </form>
        </div>
      </>
    );
  }
}

export default HomePage;

